#include<stdio.h>
int main(){
    int num1 = 5;
    printf("Initial value of num1 is %d\n", num1);
    printf("value after  post-increment is (num1++):%d\n",num1++);
    printf("Value of num1 after post-increment :%d\n",num1);
    printf("Value after pre-increment is (++num1):%d\n",++num1);
    return 0;
}
